export class EnvProvider extends Component {
    state = {
        statusUrl: ""
    };

    componentDidMount() {
        if(process.env.NODE_ENV === "development") {
            this.setState({ statusUrl: "http://localhost:3000" })
        } else {
            this.setState({ statusUrl: "https://word-processor.now.sh" })
        }
    }

    render() {

        const { statusUrl } = this.state;

        return (
            <EnvContext.Provider
                value={statusUrl}
            >
                {this.props.children}
            </EnvContext.Provider>
        )
    }
}
