import React, { useState, useEffect } from "react";
import Navbar from "./comps/navbar";
import "../public/static/css/post.scss";

const Post = () => {

    const [docName, setDocName] = useState("Untitled Document");

    const handleDocName = e => {
        setDocName(e.target.value.replace(/Untitled Document/));
    }

    useEffect(() => {
       console.log(window.getSelection()) 
    })


    return (
        <div className="postMother">
            <Navbar/>
            <div className="postMain">
                <div className="editOptionsParent">
                <div className="docNameInputDiv">
                    <input value={docName} onClick={this.setSelectionRange(0, this.value.length)} onChange={e => handleDocName(e)} className="docNameInput" placeholder="Untitled Document"/>
                </div>
                </div>
                <div className="postInputParent">
                    <textarea placeholder="Begin typing here..."></textarea>
                </div>
            </div>
        </div>
    )
}

export default Post
