import React, { createContext, useEffect, useState } from "react";

export const EnvContext = createContext();

export const useEnv = useContext(EnvContext);

export const EnvProvider = props => {

    const [statusUrl, setStatusUrl] = useState("thisshouldshowup");

    useEffect(() => {
        if(process.env.NODE_ENV === "development") {
            setStatusUrl("http://localhost:3000")
        } else {
            setStatusUrl("https://word-processor.now.sh")
        }
    }, [process.env.NODE_ENV])

    return (
        <EnvContext.Provider value={statusUrl}>
            {console.log("This is what the app sees: " + statusUrl)}
            {props.children}
        </EnvContext.Provider>
    )
}

