import React, { useState, useEffect } from "react";
import Navbar from "./comps/navbar";
import "../public/static/css/post.scss";

const Post = () => {

    const [docName, setDocName] = useState("");

    const handleDocName = e => {
        setDocName(e.target.value);
    }

    const getSelection = e => {
        let text = e.target.value;
        for(let i = 0; i < text.length; i++) {
            if(text.contains(window.getSelection().toString())) {
                console.log("Yes it contains")
            }
        }
        console.log(window.getSelection().toString())
    }

    return (
        <div className="postMother">
            <Navbar/>
            <div className="postMain">
                <div className="editOptionsParent">
                <div className="docNameInputDiv">
                    <input value={docName} onChange={e => handleDocName(e)} className="docNameInput" placeholder="Untitled Document"/>
                </div>
                </div>
                <div className="postInputParent">
                    <textarea onMouseUp={e => getSelection(e)} placeholder="Begin typing here..." minlength="10"></textarea>
                </div>
            </div>
        </div>
    )
}

export default Post
