const App = props => {
  const { Component, pageProps } = props;

  return (
    <Auth0Provider
      domain={process.env.DOMAIN}
      clientId={process.env.CLIENT_ID}
      redirectUri={process.env.REDIRECT_URI}
    >
      <EnvProvider>
        <Component {...pageProps} />
      </EnvProvider>
    </Auth0Provider>
  );
};

export default App;
